import React, { Component } from 'react'
import axios from 'axios'

import { Col, Button, Form, FormGroup, FormControl, ControlLabel, Tooltip } from "react-bootstrap"

const left = 3, right = 12-left;
const baseURL= 'http://localhost:8001';

class LogIn extends Component {

  constructor(props) {
    super(props)
    this.state = {
      email: '',
      password: ''
    }
  }
  /*
  openModal = () => {
    this.setState({
        showModal: true
    })
  }

  closeModal = () => {
    this.setState({
        showModal: false
    })
  }
  //*/

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  login = (e) => {
    e.preventDefault()
    if (this.state.email==="" || this.state.password==="") return;
    console.log(this.state)

    axios.post(`${baseURL}/users/login`,
      {email: this.state.email,
      password: this.state.password})
    .then(response=>{
      localStorage.token = response.data.token;
      this.props.handleLogIn()
      this.props.close()
    })
    .catch(err=>console.log(err));

  }

  render () {
    return (

<Form horizontal>
  <FormGroup controlId="loginEmail">
    <Col componentClass={ControlLabel} sm={left}>
      Email
    </Col>
    <Col sm={right}>
      <FormControl name="email" type="email" placeholder="Email" onChange={this.handleInput}/>
    </Col>
    {/* <Tooltip></Tooltip> */}
  </FormGroup>

  <FormGroup controlId="loginPassword">
    <Col componentClass={ControlLabel} sm={left}>
      Password
    </Col>
    <Col sm={right}>
      <FormControl name="password" type="password" placeholder="Password" onChange={this.handleInput}/>
    </Col>
  </FormGroup>

  <FormGroup>
    <Col smOffset={left} sm={right}>
      <Button type="submit" onClick={this.login}>
        Sign in
      </Button>
    </Col>
  </FormGroup>
</Form>

    )
  }
}

export default LogIn
